mapper.select:
select tb_car.id carId,camera_gun_id,tb_channel.id channelId,tb_channel.name channelName, shooting_date,shooting_time,license_plate,license_plate_color,status, tb_car.speed carSpeed,tb_channel.speed channelSpeed, tb_camera_gun.name cameraGunName,tb_camera_gun.location cameraGunLocation
from tb_car,tb_camera_gun,tb_channel where tb_car.camera_gun_id = tb_camera_gun.id and tb_channel.id = tb_camera_gun.channel_id
and shooting_date BETWEEN #{speedDTO.startTime} AND #{speedDTO.endTime}
and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})
and status = #{speedDTO.status}
and license_plate like concat('%',#{speedDTO.licensePlate},'%')
and tb_car.speed >= #{speedDTO.speed}
order by hour_time desc

select tb_car.id carId,camera_gun_id,tb_channel.id channelId,tb_channel.name channelName, shooting_date,shooting_time,license_plate,license_plate_color,status, tb_car.speed carSpeed,tb_channel.speed channelSpeed, tb_camera_gun.name cameraGunName,tb_camera_gun.location cameraGunLocation from tb_car,tb_camera_gun,tb_channel where tb_car.camera_gun_id = tb_camera_gun.id and tb_channel.id = tb_camera_gun.channel_id
and shooting_date = #{speedDTO.startTime}
and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})
and status = #{speedDTO.status}
and license_plate like concat('%',#{speedDTO.licensePlate},'%')
and tb_car.speed >= #{speedDTO.speed}
order by hour_time desc

select (select count(*) from tb_car where status = 1

    and shooting_date BETWEEN #{speedDTO.startTime} AND #{speedDTO.endTime}


    and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})

)
as overSpeed
,COUNT(*) as total
from tb_car


        shooting_date BETWEEN #{speedDTO.startTime} AND #{speedDTO.endTime}


        and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})

select (select count(*) from tb_car where status = 1

    and shooting_date = #{speedDTO.startTime}


    and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})

) as overSpeed
,COUNT(*) as total

from tb_car


        shooting_date = #{speedDTO.startTime}


        and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})

select (select count(*) from tb_car where status = 1


    and shooting_date = #{speedDTO.startTime}


    and hour_time = #{hour}


    and camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})

) as overSpeed
,COUNT(*) as total

from tb_car


        shooting_date = #{speedDTO.startTime} and


        hour_time = #{hour} and


        camera_gun_id in (select id from tb_camera_gun where channel_id = #{speedDTO.channelId})
